                {
                    treeViewReportes.Nodes["Constancias"].Nodes["ConstanciaTaller"].Remove();
                }

                if (Clase_Sesion.campus != "16" && Clase_Sesion.campus != "36" && Clase_Sesion.campus != "37" && Clase_Sesion.campus != "38" && Clase_Sesion.campus != "43" &&
                    Clase_Sesion.campus != "45" && Clase_Sesion.campus != "47")
                {
                    treeViewReportes.Nodes["Boletas"].Nodes["BoletaComplementaria"].Remove();
                }

                if (Clase_Sesion.campus != "31")
                {
                    treeViewReportes.Nodes["Informes"].Nodes["Reconocimiento"].Remove();
                }

                if (Clase_Sesion.Campus != "02" && Clase_Sesion.Rol != "14")
                {
                    treeViewReportes.Nodes["DocumentacionOficial"].Nodes["Certificados"].Nodes["CertParcialNodo"].Nodes["ParcialGradoNodo"].Nodes["CertificadoParcialdeGrado"].Remove();
                }

                if (Clase_Sesion.Campus != "01")
                {
                    treeViewReportes.Nodes["Informes"].Nodes["AlumnosSistemaLobos"].Nodes["AlumnosXEspecialidadXCEscolar"].Remove();
                    treeViewReportes.Nodes["Informes"].Nodes["AlumnosSistemaLobos"].Nodes["AlumnosXModalidadXCEscolar"].Remove();
                }
                else
                {
                    if (Clase_Sesion.Rol != "14" && Clase_Sesion.Rol != "26" && Clase_Sesion.Rol != "12")
                    {
                        treeViewReportes.Nodes["Informes"].Nodes["AlumnosSistemaLobos"].Nodes["AlumnosXEspecialidadXCEscolar"].Remove();
                        treeViewReportes.Nodes["Informes"].Nodes["AlumnosSistemaLobos"].Nodes["AlumnosXModalidadXCEscolar"].Remove();
                    }
