                if (Clase_Sesion.Campus != "39" && Clase_Sesion.Campus != "32")
                    treeViewReportes.Nodes["DocumentacionOficial"].Nodes["ActasExamen"].Nodes["SupresionExamen"].Remove();
            }
            catch (Exception ex)
            {
                MessageBox.Show(ex.Message);
            }

            //LLENA COMBOBOX CICLO ESCOLAR
            GetCicloEscolar("1");
            progressBar1.Visible = false;
        }
