        private void CBoxGrupo_SelectionChangeCommitted(object sender, EventArgs e)
        {
            string grupo = cBoxGrupo.SelectedValue.ToString();
            gridViewAlumnos.Rows.Clear();
            Clase_Alumnos alu = new Clase_Alumnos();
            Clase_Alumnos AlumnosXGrupo = new Clase_Alumnos();
            AlumnosXGrupo.ConsultaAlumnosXGrupo(grupo);
            Clase_Alumnos b = new Clase_Alumnos();
            b.EliminarTempAlumnos();
            for (int a = 0; a < AlumnosXGrupo.Lector.Tables[0].Rows.Count; a++)
            {
                gridViewAlumnos.Rows.Add();
                gridViewAlumnos.Rows[a].Cells[0].Value = AlumnosXGrupo.Lector.Tables[0].Rows[a][0].ToString();
                gridViewAlumnos.Rows[a].Cells[1].Value = AlumnosXGrupo.Lector.Tables[0].Rows[a][1].ToString();
                gridViewAlumnos.Rows[a].Cells[2].Value = AlumnosXGrupo.Lector.Tables[0].Rows[a][2].ToString();
                alu.InsertarTempAlumnos(AlumnosXGrupo.Lector.Tables[0].Rows[a][0].ToString());
            }
            txtNoAlumnos.Text = AlumnosXGrupo.Lector.Tables[0].Rows.Count.ToString();
        }
        private void BtnbuscarAlumno_Click(object sender, EventArgs e)
        {
            FrmBusquedas ObjBusqueda = new FrmBusquedas(1);
            Clase_Alumnos a = new Clase_Alumnos();
            if (ObjBusqueda.ShowDialog(this) == DialogResult.OK)
            {
                if (!String.IsNullOrEmpty(ObjBusqueda.IdPersona))
                {
                    gridViewAlumnos.Rows.Add(new string[] { ObjBusqueda.IdCodigo, ObjBusqueda.Nombre, ObjBusqueda.Status });
                    a.InsertarTempAlumnos(ObjBusqueda.IdCodigo);
                }
            }
        }
