                    WS.Cells[rowPie, 8] = dtCalificaciones["dtCalificaciones1"].Rows[0]["JefeSEPYC"].ToString() + "\r\n" + dtCalificaciones["dtCalificaciones1"].Rows[0]["SEXODIRECTOR"].ToString() + " DE REGISTRO Y CONTROL ESCOLAR";
                    WS.Range[WS.Cells[rowPie, 8], WS.Cells[rowPie, 13]].Font.Size = 8;
                    WS.Range[WS.Cells[rowPie, 8], WS.Cells[rowPie, 13]].Font.Bold = true;
                    WS.Cells[rowPie, 8].HorizontalAlignment = Microsoft.Office.Interop.Excel.XlHAlign.xlHAlignCenter;
                    WS.Cells[rowPie, 8].VerticalAlignment = Microsoft.Office.Interop.Excel.XlHAlign.xlHAlignCenter;

                    #endregion "Jefa de departamento"

                    #region "Sello de departamento"

                    // Se declaran los rangos de celdas en las que va el
                    WS.Range[WS.Cells[noFila, 15], WS.Cells[noFila, 17]].Merge();
                    WS.Cells[noFila, 15] = "SELLO DEL DEPTO. DE REGISTRO" + "\r\n" + "Y CONTROL ESCOLAR";
                    WS.Range[WS.Cells[noFila, 15], WS.Cells[noFila, 17]].Font.Size = 8;
                    WS.Range[WS.Cells[noFila, 15], WS.Cells[noFila, 17]].Font.Bold = true;
                    WS.Cells[noFila, 15].HorizontalAlignment = Microsoft.Office.Interop.Excel.XlHAlign.xlHAlignCenter;
                    WS.Cells[noFila, 15].VerticalAlignment = Microsoft.Office.Interop.Excel.XlHAlign.xlHAlignCenter;

                    #endregion "Sello de departamento"

                    #endregion "Pie de página"

                    cantRegistros = (cantRegistros - 17);
                    noFila = (rowPie + 1);
                }
                // Se verifica que exista el repositorio donde se guardara el archivo, si no existe se crea.
                if (!Directory.Exists("C:\\LoboOne\\Reportes"))
                {
                    Directory.CreateDirectory("C:\\LoboOne\\Reportes");
                }

                // Se guarda el archivo en una ruta especifica.
                string Ruta = "C:\\LoboOne\\Reportes\\SEPYCCALIFICACIONES_" + DateTime.Now.ToString("yyyyMMddHHMMss") + ".xlsx";
                WB.SaveAs(Ruta, misValue, misValue, misValue, misValue, misValue, XlSaveAsAccessMode.xlExclusive, misValue, misValue, misValue, misValue, misValue);

                // Se pregunta si se quiere abrir el archivo generado.
                DialogResult resultado = MessageBox.Show("Puedes consultar el archivo creado en la ruta:\n" + "C:\\LoboOne\\Reportes. \n" +
                    "¿Deseas abrir el archivo?", "Archivo creado y guardado", MessageBoxButtons.YesNo, MessageBoxIcon.Information);

                if (resultado == DialogResult.Yes)
                {
                    if (File.Exists(Ruta))
                    {
                        Excel.Visible = true;
                        Marshal.ReleaseComObject(WS);
                        Marshal.ReleaseComObject(WB);
                        Marshal.ReleaseComObject(Excel);
                    }
                    else
                    {
                        WB.Close(true, misValue, misValue);
                        Excel.Quit();
                        Marshal.ReleaseComObject(WS);
                        Marshal.ReleaseComObject(WB);
                        Marshal.ReleaseComObject(Excel);
                    }
                }
                else
                {
                    Excel.Visible = false;
                    WB.Close(true, misValue, misValue);
                    Excel.Quit();
                    Marshal.ReleaseComObject(WS);
                    Marshal.ReleaseComObject(WB);
                    Marshal.ReleaseComObject(Excel);
                }
            }
